export * from "./lib/MobileAppRouteProvider"

console.debug("app-mobile: dirname => " + __dirname);

import * as di from "@jorge/lib-di";
import { Container } from "container-ioc";
import { ContainerConfiguration } from "@jorge/lib-di";
import { TRouteProvider } from "@jorge/lib-webapp-core";
import { MobileAppRouteProvider } from "./lib/MobileAppRouteProvider";

class Module implements di.IModule {
  registerServices(container: ContainerConfiguration): void {
    container.registerCollection(TRouteProvider, MobileAppRouteProvider);
  }
}

console.debug("app-mobile: registering modules");
di.registerModule(new Module());