export * from "./lib/TestAppRouteProvider";

import * as di from "@jorge/lib-di";
import { Container } from "container-ioc";
import { ContainerConfiguration } from "@jorge/lib-di";
import { TRouteProvider } from "@jorge/lib-webapphost";
import { TestAppRouteProvider } from "./lib/TestAppRouteProvider";

class AppTestModule implements di.IModule {
  registerServices(container: ContainerConfiguration): void {
    container.registerCollection(TRouteProvider, TestAppRouteProvider);
  }
}

console.debug("app-test: registering modules");
di.registerModule(new AppTestModule());