import { IDeviceInformation } from "./interfaces";

export class DeviceInformation implements IDeviceInformation {
  readonly isDesktopDevice: boolean;

  constructor(
    readonly ua: string,
    readonly os: string,
    readonly device: string,
    readonly browser: string,
    readonly isMobileDevice: boolean
  ) {
    this.isDesktopDevice = !isMobileDevice;
  }
}
