export * from "./lib/IRouteProvider";
export * from "./lib/IExpressRouteProvider";
export * from "./lib/IWebApplicationHost";
export * from "./lib/WebApplicationHost";
export * from "./lib/symbols";

import * as di from "@jorge/lib-di";
import { Container, LifeTime } from "container-ioc";
import { WebApplicationHost } from "./lib/WebApplicationHost";
import { TWebApplicationHost } from "./lib/symbols";

class WebAppHostModule implements di.IModule {
  registerServices(container: di.ContainerConfiguration): void {
    container.registerSingleton(TWebApplicationHost, WebApplicationHost);
  }
}

di.registerModule(new WebAppHostModule());
