import * as di from '@jorge/lib-di';
import { Container } from "container-ioc";
import { WebApplicationHost } from "./WebApplicationHost";

export const TApplication = Symbol("IWebApplicationHost");

class Module implements di.IModule {
  registerServices(container: Container): void {
    container.register([{ token: TApplication, useClass: WebApplicationHost }]);
  }
}

di.registerModule(new Module());
