import * as express from 'express';

export interface IMarketDetectionService
{
    detectMarket(req: express.Request): IMarket;
}

export interface IMarketDetector
{
    readonly index: number;
    
    detectMarketFromRequest(req: express.Request): IMarket;
}

export interface IMarket{
    readonly marketCode : string;
    readonly source: MarketSource;
}

export enum MarketSource {
  Unknown = 0,
  Cookie,
  Url,
  Ip,
  Browser,  
  Header,
  Jurisdiction
};